'use client';

import { memo, useState } from 'react';
import Image from 'next/image';

interface OptimizedBlogImageProps {
  src: string;
  alt: string;
  fill?: boolean;
  className?: string;
  sizes?: string;
  priority?: boolean;
  width?: number;
  height?: number;
}

const OptimizedBlogImage = memo(({ 
  src, 
  alt, 
  fill = false,
  className = '',
  sizes = '100vw',
  priority = false,
  width,
  height
}: OptimizedBlogImageProps) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [hasError, setHasError] = useState(false);

  const handleLoad = () => {
    setIsLoaded(true);
  };

  const handleError = () => {
    setHasError(true);
    setIsLoaded(true);
  };

  if (hasError || !src) {
    return null;
  }

  return (
    <>
      {/* Loading skeleton */}
      {!isLoaded && (
        <div className="absolute inset-0 bg-gray-200 animate-pulse">
          <div className="w-full h-full bg-gradient-to-r from-gray-200 via-gray-100 to-gray-200 animate-pulse" />
        </div>
      )}
      
      <Image
        src={src}
        alt={alt}
        fill={fill}
        width={width}
        height={height}
        className={`transition-opacity duration-300 ${
          isLoaded ? 'opacity-100' : 'opacity-0'
        } ${className}`}
        sizes={sizes}
        priority={priority}
        quality={85}
        onLoad={handleLoad}
        onError={handleError}
        placeholder="blur"
        blurDataURL="data:image/jpeg;base64,"
      />
    </>
  );
});

OptimizedBlogImage.displayName = 'OptimizedBlogImage';

export { OptimizedBlogImage };